mapper.select:
SELECT
u.correo as correo_usuario, u.nombre as nombre_usuario, u.passwd as passwd_usuario
FROM usuario as u WHERE u.correo=#{correo} and u.passwd=#{passwd}

SELECT
u.correo as correo_usuario, u.nombre as nombre_usuario, u.passwd as passwd_usuario FROM usuario as u
WHERE u.correo=#{correo}

SELECT i.remitente as invitacion_remitente, i.proyecto as invitacion_proyecto, i.receptor as invitacion_receptor, p.nombre as proyecto_nombre
FROM invitacion as i LEFT JOIN proyecto as p on p.proyectoid = i.proyecto
WHERE i.receptor=#{correo}

mapper.insert:

			select COALESCE(MAX(notificacionid)+1,1) from notificacion where usuario=#{notificacion.usuario.correo}

		INSERT into notificacion (notificacionid, usuario, informacion) 
    	VALUES (#{id_notificacion}, #{usuario},#{notificacion.informacion})

mapper.update:
UPDATE usuario
SET passwd=#{passwd}
WHERE correo=#{usuario.correo}

UPDATE usuario
SET nombre=#{nombre}
WHERE correo=#{usuario.correo}

UPDATE usuario
SET correo=#{correo}
WHERE correo=#{usuario.correo}
